    // See https://docs.openvidu.io/en/stable/reference-docs/REST-API/#post-session
    createSession(sessionId) {
      return new Promise((resolve, reject) => {
        axios.post(`${this.OPENVIDU_SERVER_URL}/openvidu/api/sessions`, JSON.stringify({
          customSessionId: sessionId
        }), {
          auth: {
            username: 'OPENVIDUAPP',
            password: this.OPENVIDU_SERVER_SECRET
          }
        }).then(response => response.data).then(data => resolve(data.id)).catch(error => {
          if (error.response.status === 409) {
            resolve(sessionId);
          } else {
            console.warn(`No connection to OpenVidu Server. This may be a certificate error at ${this.OPENVIDU_SERVER_URL}`);

            if (window.confirm(`No connection to OpenVidu Server. This may be a certificate error at ${this.OPENVIDU_SERVER_URL}\n\nClick OK to navigate and accept it. If no certificate warning is shown, then check that your OpenVidu Server is up and running at "${this.OPENVIDU_SERVER_URL}"`)) {
              window.location.assign(`${this.OPENVIDU_SERVER_URL}/accept-certificate`);
            }

            reject(error.response);
          }
        });
      });
    },

    // See https://docs.openvidu.io/en/stable/reference-docs/REST-API/#post-connection
    createToken(sessionId) {
      return new Promise((resolve, reject) => {
        axios.post(`${this.OPENVIDU_SERVER_URL}/openvidu/api/sessions/${sessionId}/connection`, {}, {
          auth: {
            username: 'OPENVIDUAPP',
            password: this.OPENVIDU_SERVER_SECRET
